/*------------------------------------------------------------------------------*/#include "Types.r"#include "ElWhip.h"resource 'vers' (1) {	0x02, 0x00, release, 0x00,	verUS,	".071",	".071, Copyright \251 Eric Pooch 2014, 2015"};/* we use an MBAR resource to conveniently load all the menus */resource 'MBAR' (rMenuBar, preload) {	{ mApple, mFile, mEdit };	/* three menus */};resource 'MENU' (mApple, preload) {	mApple, textMenuProc,	AllItems & ~MenuItem2,	/* Disable dashed line, enable About and DAs */	enabled, apple,	{		"About ElWhip…",			noicon, nokey, nomark, plain;		"-",			noicon, nokey, nomark, plain	}};resource 'MENU' (mFile, preload) {	mFile, textMenuProc,	AllItems,				/* enable Quit only, program enables others */	enabled, "File",	{	/*	"New",			noicon, "N", nomark, plain;		"Open",			noicon, "O", nomark, plain;		"-",			noicon, nokey, nomark, plain;		"Close",			noicon, "W", nomark, plain;		"Save",			noicon, "S", nomark, plain;		"Save As…",			noicon, nokey, nomark, plain;		"Revert",			noicon, nokey, nomark, plain;		"-",			noicon, nokey, nomark, plain;		"Page Setup…",			noicon, nokey, nomark, plain;		"Print…",			noicon, nokey, nomark, plain;		"-",			noicon, nokey, nomark, plain;*/		"Quit",			noicon, "Q", nomark, plain	}};resource 'MENU' (mEdit, preload) {	mEdit, textMenuProc,	NoItems,				// disable everything, program does the enabling	enabled, "Edit",	 {		"Undo",			noicon, "Z", nomark, plain;		"-",			noicon, nokey, nomark, plain;		"Cut",			noicon, "X", nomark, plain;		"Copy",			noicon, "C", nomark, plain;		"Paste",			noicon, "V", nomark, plain;		"Clear",			noicon, nokey, nomark, plain	}};/* this ALRT and DITL are used as an About screen */resource 'ALRT' (rAboutAlert, purgeable) {	{40, 20, 160, 300},	rAboutAlert,	{ /* array: 4 elements */		/* [1] */		OK, visible, silent,		/* [2] */		OK, visible, silent,		/* [3] */		OK, visible, silent,		/* [4] */		OK, visible, silent	},	centerMainScreen};resource 'DITL' (rAboutAlert, purgeable) {	{ /* array DITLarray: 4 elements */		/* [1] */		{88, 180, 108, 240},		Button {			enabled,			"OK"		},		/* [2] */		{8, 8, 24, 260},		StaticText {			disabled,			"El Whip with lwip networking stack"		},		/* [3] */		{32, 8, 48, 260},		StaticText {			disabled,			"     © 2014, 2015 Eric Pooch"		},		/* [5] */		{56, 8, 72, 260},		StaticText {			disabled,			"Thanks to RetroChallenge 2014WW"		}	}};/* this ALRT and DITL are used as an error screen */resource 'ALRT' (rUserAlert, purgeable) {	{40, 20, 120, 260},	rUserAlert,	{ /* array: 4 elements */		/* [1] */		OK, visible, silent,		/* [2] */		OK, visible, silent,		/* [3] */		OK, visible, silent,		/* [4] */		OK, visible, silent	},	centerMainScreen};resource 'DITL' (rUserAlert, purgeable) {	{ /* array DITLarray: 3 elements */		/* [1] */		{50, 150, 70, 230},		Button {			enabled,			"OK"		},		/* [2] */		{10, 60, 30, 230},		StaticText {			disabled,			"ElWhip - Fatal Error occurred!"		},		/* [3] */		{8, 8, 40, 40},		Icon {			disabled,			2		}	}};#ifdef TESTING_1234/* here is the quintessential MultiFinder friendliness device, the SIZE resource */resource 'SIZE' (-1) {	dontSaveScreen,	acceptSuspendResumeEvents,	enableOptionSwitch,	canBackground,				/* we can background; we don't currently, but our sleep value */								/* guarantees we don't hog the Mac while we are in the background */	multiFinderAware,			/* this says we do our own activate/deactivate; don't fake us out */	backgroundAndForeground,	/* this is definitely not a background-only application! */	dontGetFrontClicks,			/* change this is if you want "do first click" behavior like the Finder */	ignoreChildDiedEvents,		/* essentially, I'm not a debugger (sub-launching) */	not32BitCompatible,			/* this app should not be run in 32-bit address space */	reserved,	reserved,	reserved,	reserved,	reserved,	reserved,	reserved,	kPrefSize * 1024,	kMinSize * 1024	};#endif